declare(strict_types = 1);
namespace BrowserDetectorTest\Helper;

use BrowserDetector\Helper;

/**
 * Test class for KreditCore_Class_BrowserDetector.
 * Generated by PHPUnit on 2010-09-05 at 22:13:26.
 */
class MacintoshTest extends \PHPUnit\Framework\TestCase
{
    /**
     * @dataProvider providerIsMacintoshPositive
     *
     * @param string $agent
     */
    public function testIsMacintoshPositive($agent)
    {
        $object = new Helper\Macintosh($agent);

        self::assertTrue($object->isMacintosh());
    }

    public function providerIsMacintoshPositive()
    {
        return [
            ['Links (0.98; Darwin 6.0 Power Macintosh)'],
            ['Mozilla/5.0 Gecko/20030306 Camino/0.7'],
            ['Apple-PubSub/65.28'],
        ];
    }

    /**
     * @dataProvider providerIsMacintoshNegative
     *
     * @param string $agent
     */
    public function testIsMacintoshNegative($agent)
    {
        $object = new Helper\Macintosh($agent);

        self::assertFalse($object->isMacintosh());
    }

    public function providerIsMacintoshNegative()
    {
        return [
            ['Mozilla/5.0 (Darwin; FreeBSD 5.6; en-GB; rv:1.9.1b3pre)Gecko/20081211 K-Meleon/1.5.2'],
            ['Mozilla/5.0 (Macintosh; ARM Mac OS X) AppleWebKit/538.15 (KHTML, like Gecko) Safari/538.15 Version/6.0 Raspbian/8.0 (1:3.8.2.0-0rpi27rpi1g) Epiphany/3.8.2'],
            ['Mozilla/5.0 (Windows NT 5.2) AppleWebKit/5342 (KHTML, like Gecko) Chrome/36.0.873.0 Mobile Safari/5342'],
        ];
    }
}
