declare(strict_types = 1);
namespace BrowserDetectorTest\Helper;

use BrowserDetector\Helper;

/**
 * Test class for KreditCore_Class_BrowserDetector.
 * Generated by PHPUnit on 2010-09-05 at 22:13:26.
 */
class MacintoshTest extends \PHPUnit\Framework\TestCase
{
    /**
     * @dataProvider providerIsMacintosh
     *
     * @param string $agent
     */
    public function testIsMacintosh($agent)
    {
        $object = new Helper\Macintosh($agent);

        self::assertTrue($object->isMacintosh());
    }

    public function providerIsMacintosh()
    {
        return [
            ['Links (0.98; Darwin 6.0 Power Macintosh)'],
            ['Mozilla/5.0 Gecko/20030306 Camino/0.7'],
            ['Apple-PubSub/65.28'],
            ['QuickTime\\\\xaa.7.0.4 (qtver=7.0.4;cpu=PPC;os=Mac 10.3.9)'],
            ['Safari5533.22.3 CFNetwork/438.16 Darwin/9.8.0 (Power%20Macintosh) (PowerMac12%2C1)'],
            ['Safari/6534.59.10 CFNetwork/454.12.4 Darwin/10.8.0 (i386) (MacPro4%2C1)'],
        ];
    }

    /**
     * @dataProvider providerIsNotMacintosh
     *
     * @param string $agent
     */
    public function testIsNotMacintosh($agent)
    {
        $object = new Helper\Macintosh($agent);

        self::assertFalse($object->isMacintosh());
    }

